<script lang="ts">
import { defineComponent, ref } from "vue";

export default defineComponent({
  name: "AccessibilityModal",

  setup() {
    const profilesClosed = ref(true);
    const dyslexiaFriendly = ref(false);
    const textSpacing = ref(false);
    const lineHeight = ref(false);
    const cursor = ref(false);
    const readingMask = ref(false);
    const readingGuide = ref(false);
    const contrast = ref(false);
    const biggerText = ref(false);
    const screenReader = ref(false);
    const profileBlind = ref(false);
    const profileLowVision = ref(false);
    const profileLearning = ref(false);
    const profileAdhd = ref(false);
    const profileSeizure = ref(false);
    const profileDyslexia = ref(false);
    const xlWidget = ref(false);
    const toggleProfile = (profile: string) => {
      if (profile === "blind") {
        profileBlind.value = !profileBlind.value;
        toggleScreenReader();
      } else if (profile === "dyslexia") {
        toggleDyslexiaFriendly();
        profileDyslexia.value = !profileDyslexia.value;
      } else if (profile === "lowVision") {
        toggleContrast();
        profileLowVision.value = !profileLowVision.value;
      } else if (profile === "learning") {
        toggleTextSpacing();
        profileLearning.value = !profileLearning.value;
      } else if (profile === "adhd") {
        toggleCursor();
        profileAdhd.value = !profileAdhd.value;
      } else if (profile === "seizure") {
        toggleReadingGuide();
        profileSeizure.value = !profileSeizure.value;
      }
    };

    const toggleXLWidget = () => {
      xlWidget.value = !xlWidget.value;
    };
    const toggleProfiles = () => {
      profilesClosed.value = !profilesClosed.value;
    };

    const toggleDyslexiaFriendly = () => {
      dyslexiaFriendly.value = !dyslexiaFriendly.value;
      if (dyslexiaFriendly.value) {
        document.body.classList.add("dyslexia-friendly");
      } else {
        document.body.classList.remove("dyslexia-friendly");
      }
    };

    const toggleContrast = () => {
      contrast.value = !contrast.value;
      if (contrast.value) {
        document.body.classList.add("high-contrast");
      } else {
        document.body.classList.remove("high-contrast");
      }
    };

    const toggleTextSpacing = () => {
      textSpacing.value = !textSpacing.value;
      if (textSpacing.value) {
        document.body.classList.add("textSpacing");
      } else {
        document.body.classList.remove("textSpacing");
      }
    };

    const toggleLineHeight = () => {
      lineHeight.value = !lineHeight.value;
      if (lineHeight.value) {
        document.body.classList.add("lineHeight");
      } else {
        document.body.classList.remove("lineHeight");
      }
    };

    const toggleCursor = () => {
      cursor.value = !cursor.value;
      if (cursor.value) {
        document.body.classList.add("cursor");
      } else {
        document.body.classList.remove("cursor");
      }
    };

    const toggleReadingGuide = () => {
      readingGuide.value = !readingGuide.value;
      if (readingGuide.value) {
        document.body.classList.add("readingGuide");
        document.addEventListener("mousemove", updateReadingGuide);
      } else {
        document.body.classList.remove("readingGuide");
        document.removeEventListener("mousemove", updateReadingGuide);
        const followcursorLeft = document.querySelector(".followcursorLeft");
        const followcursorRight = document.querySelector(".followcursorRight");
        const followcursorTop = document.querySelector(".followcursorTop");
        if (followcursorLeft instanceof HTMLElement) {
          followcursorLeft.style.display = "none";
        }
        if (followcursorRight instanceof HTMLElement) {
          followcursorRight.style.display = "none";
        }
        if (followcursorTop instanceof HTMLElement) {
          followcursorTop.style.display = "none";
        }
      }
    };

    const updateReadingGuide = (e: MouseEvent) => {
      const followcursorLeft = document.querySelector(".followcursorLeft");
      if (followcursorLeft instanceof HTMLElement) {
        followcursorLeft.style.display = "block";
        followcursorLeft.style.left = `${e.pageX - 100}px`;
        followcursorLeft.style.top = `${e.pageY}px`;
      }
      const followcursorRight = document.querySelector(".followcursorRight");
      if (followcursorRight instanceof HTMLElement) {
        followcursorRight.style.display = "block";
        followcursorRight.style.left = `${e.pageX}px`;
        followcursorRight.style.top = `${e.pageY}px`;
      }
      const followcursorTop = document.querySelector(".followcursorTop");
      if (followcursorTop instanceof HTMLElement) {
        followcursorTop.style.display = "block";
        followcursorTop.style.left = `${e.pageX - 25}px`;
        followcursorTop.style.top = `${e.pageY - 20}px`;
      }
    };

    const toggleReadingMask = () => {
      readingMask.value = !readingMask.value;
      const maskReadingGuideTop = document.querySelector(
        ".maskReadingGuideTop"
      );
      const maskReadingGuideBottom = document.querySelector(
        ".maskReadingGuideBottom"
      );
      if (readingMask.value) {
        document.body.classList.add("readingMask");
        document.addEventListener("mousemove", updateReadingMaskGuide);
        if (maskReadingGuideTop instanceof HTMLElement) {
          maskReadingGuideTop.style.display = "block";
        }
        if (maskReadingGuideBottom instanceof HTMLElement) {
          maskReadingGuideBottom.style.display = "block";
        }
      } else {
        document.body.classList.remove("readingMask");
        if (maskReadingGuideTop instanceof HTMLElement) {
          maskReadingGuideTop.style.display = "none";
        }
        if (maskReadingGuideBottom instanceof HTMLElement) {
          maskReadingGuideBottom.style.display = "none";
        }
      }
    };

    const updateReadingMaskGuide = (e: MouseEvent) => {
      const maskReadingGuideTop = document.querySelector(
        ".maskReadingGuideTop"
      );
      const maskReadingGuideBottom = document.querySelector(
        ".maskReadingGuideBottom"
      );
      if (maskReadingGuideTop instanceof HTMLElement) {
        maskReadingGuideTop.style.height = `${e.pageY - 50}px`;
      }
      if (maskReadingGuideBottom instanceof HTMLElement) {
        maskReadingGuideBottom.style.height = `${
          window.innerHeight - e.pageY - 50
        }px`;
      }
    };

    const toggleBiggerText = () => {
      biggerText.value = !biggerText.value;
      if (biggerText.value) {
        document.body.classList.add("biggerText");
      } else {
        document.body.classList.remove("biggerText");
      }
    };

    const toggleScreenReader = () => {
      screenReader.value = !screenReader.value;
      if (screenReader.value) {
        document.body.classList.add("screenReader");
      } else {
        document.body.classList.remove("screenReader");
      }
    };

    return {
      profilesClosed,
      toggleProfiles,
      contrast,
      toggleContrast,
      dyslexiaFriendly,
      toggleDyslexiaFriendly,
      textSpacing,
      toggleTextSpacing,
      lineHeight,
      toggleLineHeight,
      cursor,
      toggleCursor,
      readingMask,
      toggleReadingMask,
      readingGuide,
      toggleReadingGuide,
      biggerText,
      toggleBiggerText,
      screenReader,
      toggleProfile,
      toggleScreenReader,
      profileBlind,
      profileDyslexia,
      profileLowVision,
      profileLearning,
      profileAdhd,
      profileSeizure,
      xlWidget,
      toggleXLWidget,
    };
  },
});
</script>
